import Foundation

final class OnlineSearchModuleInteractor {
    let movieService = AlamofireMovieService(context: NonPersistentApiContext(environment: ApiEnvironment.production))
}

// MARK: - Extensions -

extension OnlineSearchModuleInteractor: OnlineSearchModuleInteractorInterface {
    func searchByTitle(term: String, completion: @escaping MoviesServiceResult) {
        movieService.searchMovieBy(title: term) { (_, apiMoviesResponse, responseError) in
            completion(apiMoviesResponse, responseError)
        }
    }
